  `description` varchar(1024) NOT NULL COMMENT '描述',
  `dropId` int(11) NOT NULL DEFAULT '0' COMMENT '掉落ID（客户端读取）',
  `cardId` int(11) NOT NULL,
  `armsId` int(11) NOT NULL,
  `armyNum` int(11) NOT NULL,
  `ArmExperience` varchar(255) NOT NULL,
