CREATE TABLE `tpl_scenario_section` (
  `id` int(11) NOT NULL COMMENT '关卡ID',
  `name` varchar(64) NOT NULL COMMENT '关卡名称(多语言配置)',
  `description` varchar(1024) NOT NULL COMMENT '关卡描述(多语言配置)',
  `descriptMap` varchar(64) NOT NULL COMMENT '关卡描述背景图片',
  `remark` varchar(64) NOT NULL COMMENT '策划备注',
  `chapterId` int(11) NOT NULL COMMENT '章节ID',
  `maxNum` int(11) NOT NULL DEFAULT '0' COMMENT '每日可挑战次数',
  `condList` varchar(128) NOT NULL COMMENT '关卡限制条件',
  `levelType` int(11) NOT NULL,
  `canRepeat` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否可以重复刷关卡(1大关卡，0小关卡)',
  `isBranch` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否是分支关卡(0不是分支，1是分支)',
  `stamina` int(11) NOT NULL COMMENT '体力消耗',
  `showRewardList` varchar(128) NOT NULL DEFAULT '' COMMENT '显示可能获得的奖励(前端用) 格式前端定',
