  `publicDropId` int(11) NOT NULL COMMENT '日常掉落',
  `extDropList` varchar(1024) NOT NULL COMMENT '限制X次必掉XXX 格式为{{物品ID，数量，概率，次数},...}',
  `rewardList` varchar(128) NOT NULL COMMENT '闯关通关奖励(城主经验,武将经验,银两...) 格式{[itemId]=count,...}',
  `mopupRewardList` varchar(128) NOT NULL COMMENT '扫荡通关奖励 格式{[itemId]=count,...}',
  `battleType` smallint(6) NOT NULL COMMENT '自定义战斗类型 1,标准战斗，2答题，3特定武将的战斗 ',
  `armyGroup` int(11) NOT NULL DEFAULT '0' COMMENT '怪物随即库（组ID）',
  `armyCount` int(11) NOT NULL COMMENT '随机数(组ID中可取的部队数)',
  `singleCombat` int(11) NOT NULL DEFAULT '0' COMMENT '是否触发单挑(1触发)',
  `singleCombatHero` varchar(64) NOT NULL COMMENT '指定单挑的武将(0不指定) 格式：{我方npcID，敌方npcID}',
  `questionPool` varchar(128) NOT NULL COMMENT '题库格式：{科目类型,数量}',
  `questionCount` int(11) NOT NULL DEFAULT '0' COMMENT '出题数量',
  `heroConf` varchar(128) NOT NULL COMMENT '我方必须出战的武将,没有则用配置 格式 {npcID,... }',
  `imageIcon` varchar(64) NOT NULL COMMENT '图标名称',
  `answerModelMy` int(11) NOT NULL COMMENT '我方答题模型(武将ID)',
  `answerModelOther` int(11) NOT NULL COMMENT '对方答题模型(武将ID)',
  `clientX` float NOT NULL COMMENT '关卡坐标x(前端用)',
  `clientY` float NOT NULL COMMENT '关卡坐标y(前端用)',
  `battleScene` varchar(128) NOT NULL COMMENT '战斗场景',
  `dialogue` varchar(1024) NOT NULL COMMENT '1战前对话，2答题前对话，3答题结束胜利，4答题结束失败 格式：{[1]={对话ID1,...};[2]={对话ID1,...}……}',
  `preId` varchar(128) NOT NULL COMMENT '前置关卡ID组 格式{关卡ID1,关卡ID2,...}',
  `nextId` varchar(128) NOT NULL COMMENT '后置关卡ID组 格式{关卡ID1,关卡ID2,...}',
  `starList` varchar(128) NOT NULL COMMENT '星级要求 1星通关条件，2星条件，3星条件',
  `resetPrice` varchar(128) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='剧情副本关卡表';
